import { useState } from "react";
import { useMutation } from "@apollo/client";
import { addBookMutation } from "../queries/queries";
import AddAuthors from "./AddAuthors";

export default function AddBook() {
  const [newBook, setNewBook] = useState({
    name: "",
    genre: "",
    authorId: "",
  });

  const [addBook, { data: data1, loading: loading1, error: error1 }] =
    useMutation(addBookMutation);

  if (loading1) return <p>Submitting...</p>;
  if (error1) return <p>Submission error! {error1.message}</p>;

  const submitForm = (e) => {
    e.preventDefault();
    console.log(newBook);
    addBook({
      variables: {
        name: newBook.name,
        genre: newBook.genre,
        authorId: newBook.authorId,
      },
    });
  };

  return (
    <form id="add-book" onSubmit={submitForm}>
      <div className="field">
        <label>Book name: </label>
        <input
          type="text"
          value={newBook.name}
          onChange={(e) => setNewBook({ ...newBook, name: e.target.value })}
        />
      </div>

      <div className="field">
        <label>Genre: </label>
        <input
          type="text"
          value={newBook.genre}
          onChange={(e) => setNewBook({ ...newBook, genre: e.target.value })}
        />
      </div>
      <AddAuthors newBook={newBook} setNewBook={setNewBook} />

      <button>+</button>
    </form>
  );
}
