import { useState } from "react";
import { useMutation } from "@apollo/client";
import { addBookMutation } from "../queries/queries";
import AddAuthors from "./AddAuthors";

export default function AddBook() {
  const [name, setName] = useState("");
  const [genre, setGenre] = useState("");
  const [authorId, setAuthorId] = useState("");

  const [addBook, { error }] = useMutation(addBookMutation);

  if (error) return <p>Submission error! {error.message}</p>;

  const createBook = (e) => {
    e.preventDefault();
    addBook({
      variables: {
        name,
        genre,
        authorId,
      },
    });
    if (error) console.log(error);
  };

  return (
    <form id="add-book" onSubmit={(e) => createBook(e)}>
      <div className="field">
        <label>Book name: </label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </div>

      <div className="field">
        <label>Genre: </label>
        <input
          type="text"
          value={genre}
          onChange={(e) => setGenre(e.target.value)}
        />
      </div>
      <AddAuthors newBook={newBook} setNewBook={setNewBook} />

      <button>+</button>
    </form>
  );
}
