import { useState } from "react";
import { useQuery, useMutation } from "@apollo/client";
import { getAuthorsQuery, addBookMutation } from "../queries/queries";

export default function AddBook() {
  const [newBook, setNewBook] = useState({
    name: "",
    genre: "",
    authorId: "",
  });

  const [addBook, { data: data1, loading: loading1, error: error1 }] =
    useMutation(addBookMutation);

  if (loading1) return "Submitting...";
  if (error1) return `Submission error!`;

  const submitForm = (e) => {
    e.preventDefault();
    console.log(newBook);
    return addBook({ variables: newBook });
  };

  const { loading, error, data } = useQuery(getAuthorsQuery);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  const authors = data?.authors;
  return (
    <form id="add-book" onSubmit={submitForm}>
      <div className="field">
        <label>Book name: </label>
        <input
          type="text"
          value={newBook.name}
          onChange={(e) => setNewBook({ ...newBook, name: e.target.value })}
        />
      </div>

      <div className="field">
        <label>Genre: </label>
        <input
          type="text"
          value={newBook.genre}
          onChange={(e) => setNewBook({ ...newBook, genre: e.target.value })}
        />
      </div>

      <div className="field">
        <label>Author: </label>
        <select
          value={newBook.authorId}
          onChange={(e) => setNewBook({ ...newBook, authorId: e.target.value })}
        >
          <option>Select author</option>
          {authors.map((author) => (
            <option key={author.id} value={author.id}>
              {author.name}
            </option>
          ))}
        </select>
      </div>

      <button>+</button>
    </form>
  );
}
